SELECT CAST( SUM( CASE WHEN DATE_FORMAT( CAST( `date` AS DATETIME ) , '%Y' ) = '2010' THEN 1 ELSE 0 END ) AS DOUBLE ) * 100 / COUNT( `id` ) - CAST( SUM( CASE WHEN DATE_FORMAT( CAST( `date` AS DATETIME ) , '%Y' ) = '2011' THEN 1 ELSE 0 END ) AS DOUBLE ) * 100 / COUNT( `id` ) FROM `badges` WHERE `name` = 'Student'
