SELECT "t2"."forename" , "t2"."surname" FROM "qualifying" AS "t1" INNER JOIN "drivers" AS "t2" ON "t1"."driverid" = "t2"."driverid" INNER JOIN "races" AS "t3" ON "t1"."raceid" = "t3"."raceid" WHERE "q3" IS NOT NULL AND "t3"."year" = 2008 AND "t3"."circuitid" IN ( SELECT "circuitid" FROM "circuits" WHERE "name" = 'Marina Bay Street Circuit' ) ORDER BY CAST( SPLIT_PART( "q3" , ':' , 1 ) AS INTEGER ) * 60 + CAST( SPLIT_PART( SPLIT_PART( "q3" , ':' , 2 ) , '.' , 1 ) AS REAL ) + CAST( SPLIT_PART( "q3" , '.' , 2 ) AS REAL ) / 1000 ASC LIMIT 1
