SELECT DISTINCT EXTRACT( YEAR FROM AGE( NOW( ) , "t2"."birthday" :: timestamp ) ) + ( CASE WHEN EXTRACT( MONTH FROM AGE( NOW( ) , "t2"."birthday" :: timestamp ) ) > 0 OR EXTRACT( DAY FROM AGE( NOW( ) , "t2"."birthday" :: timestamp ) ) > 0 THEN 1 ELSE 0 END ) AS "age" FROM "player_attributes" AS "t1" INNER JOIN "player" AS "t2" ON "t1"."player_api_id" = "t2"."player_api_id" WHERE EXTRACT( YEAR FROM "t1"."date" :: date ) BETWEEN 2013 AND 2015 AND "t1"."sprint_speed" >= 97
