SELECT "t1"."account_id" , ( SELECT MAX( "a11" ) - MIN( "a11" ) FROM "district" ) FROM "account" AS "t1" INNER JOIN "district" AS "t2" ON "t1"."district_id" = "t2"."district_id" INNER JOIN "disp" AS "t3" ON "t1"."account_id" = "t3"."account_id" INNER JOIN "client" AS "t4" ON "t3"."client_id" = "t4"."client_id" WHERE "t2"."district_id" = ( SELECT "district_id" FROM "client" WHERE "gender" = 'F' ORDER BY "birth_date" ASC NULLS FIRST LIMIT 1 ) ORDER BY "t2"."a11" DESC NULLS LAST LIMIT 1
