SELECT "t1"."name" , "t1"."artist" , "t1"."ispromo" FROM "cards" AS "t1" INNER JOIN "rulings" AS "t2" ON "t1"."uuid" = "t2"."uuid" WHERE "t1"."ispromo" = 1 AND "t1"."artist" = ( SELECT "artist" FROM "cards" WHERE "ispromo" = 1 GROUP BY "artist" HAVING COUNT( DISTINCT "uuid" ) = ( SELECT MAX( "count_uuid" ) FROM ( SELECT COUNT( DISTINCT "uuid" ) AS "count_uuid" FROM "cards" WHERE "ispromo" = 1 GROUP BY "artist" ) AS "subquery" ) ) 
