SELECT "t"."element" FROM ( SELECT "t1"."element" , COUNT( DISTINCT "t1"."molecule_id" ) FROM "atom" AS "t1" INNER JOIN "molecule" AS "t2" ON "t1"."molecule_id" = "t2"."molecule_id" WHERE "t2"."label" = '-' GROUP BY "t1"."element" ORDER BY COUNT( DISTINCT "t1"."molecule_id" ) ASC NULLS FIRST  ) AS "t"
