SELECT CAST( SUM( CASE WHEN "t1"."player_name" = 'Abdou Diallo' THEN "t2"."ball_control" ELSE 0 END ) AS REAL ) / NULLIF( COUNT( CASE WHEN "t1"."player_name" = 'Abdou Diallo' THEN "t2"."id" ELSE NULL END ) , 0 ) - CAST( SUM( CASE WHEN "t1"."player_name" = 'Aaron Appindangoye' THEN "t2"."ball_control" ELSE 0 END ) AS REAL ) / NULLIF( COUNT( CASE WHEN "t1"."player_name" = 'Aaron Appindangoye' THEN "t2"."id" ELSE NULL END ) , 0 ) FROM "player" AS "t1" INNER JOIN "player_attributes" AS "t2" ON "t1"."player_api_id" = "t2"."player_api_id"
