SELECT "teaminfo"."team_long_name" FROM "league" AS "leaguedata" INNER JOIN "match" AS "matchdata" ON "leaguedata"."id" = "matchdata"."league_id" INNER JOIN "team" AS "teaminfo" ON "matchdata"."away_team_api_id" = "teaminfo"."team_api_id" WHERE "leaguedata"."name" = 'Scotland Premier League' AND "matchdata"."season" = '2009/2010' AND "matchdata"."away_team_goal" - "matchdata"."home_team_goal" > 0 GROUP BY "teaminfo"."team_long_name" , "matchdata"."away_team_api_id" ORDER BY COUNT( * ) DESC 
